import axios from "axios";
import React, { useEffect, useState } from "react";

export const SprintList = () => {
    const [sprints, setSprints] = useState({});

    const fetchSprints = async () => {
        const res = await axios.get('http://localhost:3001/sprints');

        setSprints(res.data);
    };

    useEffect(() => {
        fetchSprints();
    }, []);

    const renderedSprints = Object.values(sprints).map(sprint => {
        return <div 
        className="card" 
        style={{ width: '30%', marginBottom: '20px' }}
        key={sprint.id}>
            <div className="card-body">
                <h3>{sprint.title}</h3>
                {/* <CommentList sprintId={sprint.comments}/>
                <CommentCreate sprintId={sprint.id}/> */}
            </div>
        </div>
    });

    return <div className="d-flex flex-row flex-wrap">
        {renderedSprints}
    </div>
};
