import React, { useState, useEffect } from "react";
import axios from 'axios';

export const CommentList = ({ postComments }) => {

    console.log('comments -', postComments);

    const renderedComments = postComments?.map(comment => {
        if(comment.status === 'approved') {
            return <li key={comment.id}>{comment.content}</li>;
        } else if(comment.status === 'pending') {
            return <li key={comment.id}><i>This comment is pending approval.</i></li>;
        } else {
            return <li key={comment.id}><i>This comment was removed.</i></li>;
        }
    });

    return <ul>{renderedComments}</ul>
};
